import './index.scss'
import React, {useState} from "react";
import classNames from "classnames";
import TokenLogo from "../../../SvgIcon/TokenLogo";
import InfoTooltip from "../../../UI/InfoTooltip";

interface IBuyTokens {
    buyMc: (amount: string, refId: string) => void;
    wallet: any,
    balance: string
}

const BuyTokens: React.FC<IBuyTokens> = ({buyMc, wallet, balance}) => {
    const isMobile = window.innerWidth < 1366
    const [amount, setAmount] = useState('')
    const [refId, setRefId] = useState('')
    const [allowance, setAllowance] = useState('')

    const disabledButton = Number(amount) > Number(balance)

    const buy = () => {
        buyMc(amount, refId)
    }

    const approveMock = async () => {
        await wallet.approveToken(amount);
    }

    const setA = async () => {
        try {
            const _allowance = await wallet.getAllowance();
            console.log(_allowance);
            setAllowance(_allowance);
        } catch (e) {
            console.error(e)
        }
    }

    const checkAllowance = () => {
        console.log(allowance);
        console.log(amount);
        const a = Number(allowance) >= Number(amount);
        console.log("AAAAAA", a);
        return a;   
    }

    const handleChangeAmount = (e: any) => {
        setAmount(e.target.value);
        setA();
    }

    if (isMobile) {
        return (
            <div className={'landing-buyTokens'}>
                <div className={'buy-token'}>
                    <div className={'buy-token__header'}>
                        <div className={'buy-token__header-logo'}>
                            <TokenLogo/>
                        </div>
                    </div>
                    <div className={'buy-token__body'}>
                        <p>1 MSMART = 1 USDT</p>
                        <div className={'buy-token__body-input'}>
                            <div className={'deposit-popup__deposit-input'}>
                                <input
                                    type={'number'}
                                    placeholder={'Введите Сумму'}
                                    value={amount}
                                    onChange={(e) => handleChangeAmount(e)}
                                />
                            </div>
                        </div>

                        <div className={'buy-token__body-referral'}>
                            <InfoTooltip text={'Реферальный код может быть использован единожды при первой покупке'}>
                                <p>Реферальный код</p>
                            </InfoTooltip>
                            <div className={'deposit-popup__referral-input'}>
                                <input
                                    placeholder={'00000000'}
                                    value={refId}
                                    onChange={(e) => setRefId(e.target.value)}
                                />
                            </div>
                        </div>

                        <div className={classNames({
                            'ui-button-v2': true,
                            'ui-button-v2--disabled': disabledButton || !wallet,
                        })}
                             onClick={() => !disabledButton && wallet && buy()}
                        >
                            <p>Купить</p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

    if (!checkAllowance()) return (
        <div className={'landing-buyTokens'}>
            <div className={'buy-token'}>
                <div className={'buy-token__header'}>
                    <div className={'buy-token__header-logo'}>
                        <TokenLogo/>
                    </div>
                </div>
                <div className={'buy-token__body'}>
                    <p>1 MSMART = 1 USDT</p>
                    <div className={'buy-token__body-input'}>
                        <div className={'deposit-popup__deposit-input'}>
                            <input
                                type={'number'}
                                placeholder={'Введите Сумму'}
                                value={amount}
                                onChange={(e) => handleChangeAmount(e)}
                            />
                        </div>
                    </div>
                    <div className={'buy-token__body-referral'}>
                        <InfoTooltip text={'Реферальный код может быть использован единожды при первой покупке'}>
                            <p>Реферальный код</p>
                        </InfoTooltip>
                        <div className={'deposit-popup__referral-input'}>
                            <input
                                placeholder={'00000000'}
                                value={refId}
                                onChange={(e) => setRefId(e.target.value)}
                            />
                        </div>
                    </div>
                    <div className={classNames({
                        'ui-button-v2': true,
                    })}
                    onClick={() => approveMock()}
                    >
                        <p>Одобрить</p>
                    </div>
                    <div className={classNames({
                        // 'ui-button-v2': true,
                        'ui-button-v2': disabledButton || !wallet,
                    })}
                    >
                        <p>Купить</p>
                    </div>
                </div>
            </div>
        </div>
    )

    return (
        <div className={'landing-buyTokens'}>
            <div className={'buy-token'}>
                <div className={'buy-token__header'}>
                    <div className={'buy-token__header-logo'}>
                        <TokenLogo/>
                    </div>
                </div>
                <div className={'buy-token__body'}>
                    <p>1 MSMART = 1 USDT</p>
                    <div className={'buy-token__body-input'}>
                        <div className={'deposit-popup__deposit-input'}>
                            <input
                                type={'number'}
                                placeholder={'Введите Сумму'}
                                value={amount}
                                onChange={(e) => handleChangeAmount(e)}
                            />
                        </div>
                    </div>
                    <div className={'buy-token__body-referral'}>
                        <InfoTooltip text={'Реферальный код может быть использован единожды при первой покупке'}>
                            <p>Реферальный код</p>
                        </InfoTooltip>
                        <div className={'deposit-popup__referral-input'}>
                            <input
                                placeholder={'00000000'}
                                value={refId}
                                onChange={(e) => setRefId(e.target.value)}
                            />
                        </div>
                    </div>
                    <div className={classNames({
                        'ui-button-v2': true,
                        'ui-button-v2--disabled': disabledButton || !wallet,
                    })}
                         onClick={() => (!disabledButton && wallet) && buy()}
                    >
                        <p>Купить</p>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default BuyTokens;
