import './index.scss'
import React, {useState} from "react";
import classNames from "classnames";
import TokenLogo from "../../../SvgIcon/TokenLogo";
import InfoTooltip from "../../../UI/InfoTooltip";

interface IBuyTokens {
    buyMc: (amount: string, refId: string) => void;
    getAllowance: () => Promise<string>;
    wallet: any,
    balance: string
}

const BuyTokens: React.FC<IBuyTokens> = ({buyMc, getAllowance, wallet, balance}) => {
    const isMobile = window.innerWidth < 1366
    const [amount, setAmount] = useState('')
    const [refId, setRefId] = useState('')

    const disabledButton = Number(amount) > Number(balance)

    const buy = () => {
        buyMc(amount, refId)
    }

    const checkAllowance = () => {
        return Number(getAllowance()) >= Number(amount);
    }

    const handleChangeAmount = (e: any) => {
        setAmount(e.target.value)
    }

    if (isMobile) {
        return (
            <div className={'landing-buyTokens'}>
                <div className={'buy-token'}>
                    <div className={'buy-token__header'}>
                        <div className={'buy-token__header-logo'}>
                            <TokenLogo/>
                        </div>
                    </div>
                    <div className={'buy-token__body'}>
                        <p>1 MSMART = 1 USDT</p>
                        <div className={'buy-token__body-input'}>
                            <div className={'deposit-popup__deposit-input'}>
                                <input
                                    type={'number'}
                                    placeholder={'Введите Сумму'}
                                    value={amount}
                                    onChange={(e) => handleChangeAmount(e)}
                                />
                            </div>
                        </div>

                        <div className={'buy-token__body-referral'}>
                            <InfoTooltip text={'Реферальный код может быть использован единожды при первой покупке'}>
                                <p>Реферальный код</p>
                            </InfoTooltip>
                            <div className={'deposit-popup__referral-input'}>
                                <input
                                    placeholder={'00000000'}
                                    value={refId}
                                    onChange={(e) => setRefId(e.target.value)}
                                />
                            </div>
                        </div>

                        <div className={classNames({
                            'ui-button-v2': true,
                            'ui-button-v2--disabled': !disabledButton || !wallet,
                        })}
                             onClick={() => disabledButton && wallet && buy()}
                        >
                            <p>Купить</p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

    if (checkAllowance()) return (
        <div className={'landing-buyTokens'}>
            <div className={'buy-token'}>
                <div className={'buy-token__header'}>
                    <div className={'buy-token__header-logo'}>
                        <TokenLogo/>
                    </div>
                </div>
                <div className={'buy-token__body'}>
                    <p>1 MSMART = 1 USDT</p>
                    <div className={'buy-token__body-input'}>
                        <div className={'deposit-popup__deposit-input'}>
                            <input
                                type={'number'}
                                placeholder={'Введите Сумму'}
                                value={amount}
                                onChange={(e) => handleChangeAmount(e)}
                            />
                        </div>
                    </div>
                    <div className={'buy-token__body-referral'}>
                        <InfoTooltip text={'Реферальный код может быть использован единожды при первой покупке'}>
                            <p>Реферальный код</p>
                        </InfoTooltip>
                        <div className={'deposit-popup__referral-input'}>
                            <input
                                placeholder={'00000000'}
                                value={refId}
                                onChange={(e) => setRefId(e.target.value)}
                            />
                        </div>
                    </div>
                    <div className={classNames({
                        'ui-button-v2': true,
                        'ui-button-v2--disabled': !disabledButton || !wallet,
                    })}
                    >
                        <p>Не купить</p>
                    </div>
                </div>
            </div>
        </div>
    )

    return (
        <div className={'landing-buyTokens'}>
            <div className={'buy-token'}>
                <div className={'buy-token__header'}>
                    <div className={'buy-token__header-logo'}>
                        <TokenLogo/>
                    </div>
                </div>
                <div className={'buy-token__body'}>
                    <p>1 MSMART = 1 USDT</p>
                    <div className={'buy-token__body-input'}>
                        <div className={'deposit-popup__deposit-input'}>
                            <input
                                type={'number'}
                                placeholder={'Введите Сумму'}
                                value={amount}
                                onChange={(e) => handleChangeAmount(e)}
                            />
                        </div>
                    </div>
                    <div className={'buy-token__body-referral'}>
                        <InfoTooltip text={'Реферальный код может быть использован единожды при первой покупке'}>
                            <p>Реферальный код</p>
                        </InfoTooltip>
                        <div className={'deposit-popup__referral-input'}>
                            <input
                                placeholder={'00000000'}
                                value={refId}
                                onChange={(e) => setRefId(e.target.value)}
                            />
                        </div>
                    </div>
                    <div className={classNames({
                        'ui-button-v2': true,
                        'ui-button-v2--disabled': !disabledButton || !wallet,
                    })}
                         onClick={() => (disabledButton && wallet) && buy()}
                    >
                        <p>Купить</p>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default BuyTokens;
